<script lang="ts">
	import { cleanURL } from '$lib/client/util.js';

	let { data, children } = $props();

	let pattern_def = '^https:\\/\\/([a-zA-Z\\d\\-]{1,63}\\.){1,62}[a-zA-Z\\-]{1,63}$';

    let url = cleanURL(data.url);
</script>

<div class="box m-8 flex flex-row items-center justify-between rounded-2xl">
	<h1 class="!text-logo pt-4 pl-4 text-center !text-6xl">CheckDaWeb</h1>
	<form class="w-[50%] p-4" method="GET" action="search?/">
		<input
			class="bg-secondary-dark w-full border-none"
			id="url"
			name="url"
			type="text"
			pattern={pattern_def}
            value={data.url + ""}
			placeholder="e.g. https://bedless.dev"
		/>
	</form>
	<div class="flex flex-row">
		<img
			class="h-[64px] w-[64px] rounded-[10px]"
			alt="Nothing here"
			src="data:image/png;base64,"
		/>
		<p class="h-full p-4 text-center text-[1.1rem]">{url}</p>
	</div>
</div>

{@render children()}